sysauto_script.script:
//Obtener tiempo y fecha de ahora
var rightNow = new GlideDateTime();
var devolucionesAtrasadas = new GlideRecord('x_177089_todoteka_solicitudes_todoteka');

/*Busca reportes atrasados, estará totalmente habilitado cuando se le agregen los campos de estado correspondientes a los items del catalogo TodoTeka*/
devolucionesAtrasadas.addQuery('work_end', '<=', rightNow);
devolucionesAtrasadas.addQuery('state',2);//las solicitudes estan en aprobadas
devolucionesAtrasadas.addQuery('todoteka_articulo_entregado',true);//estan en préstamo
devolucionesAtrasadas.addQuery('devuelto',false);//No han sido devueltas

devolucionesAtrasadas.query();

//Pasa los reportes atrasados al estado 6(extraviado) manda notificacion a usuario
while(devolucionesAtrasadas.next()) {
	gs.addInfoMessage("Los siguientes prestamos están atrasados: " + devolucionesAtrasadas.number);
	gs.eventQueue('x_177089_todoteka.SolicitudAtrasada', devolucionesAtrasadas, devolucionesAtrasadas.number, gs.getUserName());
	//devolucionesAtrasadas.state = 6;
	//devolucionesAtrasadas.update();
}

//Busca los reportes que estaran atrasados entre las 9am y 6pm

devolucionesAtrasadas.addQuery('work_end', '>=', rightNow);
devolucionesAtrasadas.addQuery('work_end', '<', gs.hoursAgo(-9));
devolucionesAtrasadas.addQuery('state',2);
devolucionesAtrasadas.addQuery('todoteka_articulo_entregado',true);//estan en préstamo
devolucionesAtrasadas.addQuery('devuelto',false);//No han sido devueltos
devolucionesAtrasadas.query();

while(devolucionesAtrasadas.next()){
	gs.eventQueue('x_177089_todoteka.EventRecordatorio', devolucionesAtrasadas, devolucionesAtrasadas.number, gs.getUserName());
}
